import { CryptoCards, Button, ConnectButton } from "web3uikit";
import { useState } from "react";
import abi from "./contracts/Donate.json";
import { ethers } from "ethers";

function App() {
  // Contract Address & ABI
  const contractAddress = "0x4385B18589eaFcA91A5E396baFA98db0e8a4aEA7";
  const contractABI = abi.abi;

  // Component state
  const [donorAccount, setDonorAccount] = useState("");
  const [amount, setAmount] = useState("");

  const connectWallet = async () => {
    try {
      const { ethereum } = window;

      if (!ethereum) {
        console.log("please install MetaMask");
      }

      const accounts = await ethereum.request({
        method: "eth_requestAccounts",
      });

      setDonorAccount(accounts[0]);
    } catch (error) {
      console.log(error);
    }
  };

  const handleDonate = async () => {
    try {
      const { ethereum } = window;

      if (ethereum) {
        const provider = new ethers.providers.Web3Provider(ethereum, "any");
        const signer = provider.getSigner();
        const donate = new ethers.Contract(
          contractAddress,
          contractABI,
          signer
        );

        console.log("donating matic..");
        const donateTxn = await donate.depositMatic({
          value: ethers.utils.parseEther("0.001"),
        });

        await donateTxn.wait();

        console.log("mined ", donateTxn.hash);

        console.log("donation succcessfull!");

        // Clear the form fields.
      }
    } catch (error) {
      console.log(error);
    }
  };
  return (
    <>
      <header>
        <img src="/fren.png" alt="logo" />
        <div className="right-section">
          <ul>
            <li>
              <button onClick={() => connectWallet()}>Connect</button>
            </li>
          </ul>
        </div>
      </header>

      <div className="intro">
        <h1>Giving it back to where we have all started.</h1>
        <p>{donorAccount}</p>
        <p>
          Web3bridge has been a blessing for all of us. <br />
          It's time we give back to where we have all have started.
        </p>
      </div>

      <div className="input">
        <input type="number" />

        <button onClick={() => handleDonate()}>Donate</button>
      </div>

      <footer>
        Â© Handcrafted from scratch with ðŸ’– by devlongs. 
      </footer>
    </>
  );
}

export default App;
